import React,{Fragment} from "react"
import ReactSimpleCarousel from "react-plain-carousel";

class Carosal extends React.Component {
    state = {
      carousel: {},
      index: 0
    };
  
    next = () => {
      this.state.carousel.next();
    };
  
    prev = () => {
      this.state.carousel.prev();
    };
  
    moveTo = index => () => {
      this.state.carousel.moveTo(index);
    };
  
    handleMount = carousel => {
      this.setState({ carousel });
    };
  
    handleTransitionEnd = ({ index }) => {
      this.setState({ index });
    };
    list =() => {
      this.props.quotes.slice(0,2).map((quote)=>{
         return  <div style={{ height: 50, backgroundColor: "red" }}>{quote.author}</div>
      })
    }
    
    render() {
      return (
        <Fragment>
  
          {console.log(this.props.quotes)}
          <h2>Carousel 2 (controls)</h2>
          <Fragment>
            <ReactSimpleCarousel
              isInfinity
              className="carousel2"
              onMount={this.handleMount}
            >
              {this.props.quotes.slice(0,3).map((quote=> {}))}
              {/* <div style={{ height: 250, backgroundColor: "red" }}>Slide - 1</div>
              <div style={{ height: 250, backgroundColor: "green" }}>
                Slide - 2
              </div>
              <div style={{ height: 250, backgroundColor: "blue" }}>
                Slide - 3
              </div>
              <div style={{ height: 250, backgroundColor: "red" }}>Slide - 14</div>
              <div style={{ height: 250, backgroundColor: "green" }}>
                Slide - 42
              </div>
              <div style={{ height: 250, backgroundColor: "blue" }}>
                Slide - 34
              </div> */}
            </ReactSimpleCarousel>
  
            <button onClick={this.prev}>Prev</button>
            <button onClick={this.next}>Next</button>
          </Fragment>
  
          
        </Fragment>
      );
    }
  }
  export default Carosal
