import React, { useState, useEffect } from "react";
import thinker from "thinker.png"
import axios from "axios"
import Carosal from "./Carosal"

const TriviaMain = () => {

    const [activeState, setactiveState] = useState([]);
    useEffect(() => {
        const response = axios.get("https://opentdb.com/api.php?amount=10&category=18").then((res) => {
          setactiveState(res.data);
          console.log(res.data);
        });
      }, []);
    return <div className="jokes-main" style={{backgroundImage:"linear-gradient(to left bottom, #d5dc56d0, #bfdc55d7, #a4db57d3, #87da5dd5, #65d865cb, #50d772d7, #36d57ec5, #00d389bb, #09d198bb, #20cfa3c5, #34ccabcb, #48c9b3ce),url("+th+")"}}>
        
    </div>
}

export default TriviaMain
