import React, { useState } from "react";
import pic from "./defaultPic"
function Carousel({quotes}) {
  const [curItem, setCurrentItem] = useState(0);
  const items = [
    { text: "lorem" },
    { text: "ispum" },
    { text: "dolor" },
    { text: "sit" },
    { text: "amet" }
  ];

  function goToPrevItem() {
    setCurrentItem(curItem ? curItem - 1 : 0);
  }

  function goToNextItem() {
    setCurrentItem(curItem < quotes.length - 1 ? curItem + 1 : quotes.length - 1);
  }

  return (
    <div className="carousel">
      <div className="carousel-items">
        {quotes.map((item, itemIndex) => (
          <div
            className={
              itemIndex === curItem ? `carousel-item active` : `carousel-item`
            }
            key={`carousel-item-${itemIndex}`}
          >
            <h2>{item.author}</h2>
            <br/>
            <p>{item.text}</p>
          </div>
        ))}
      </div>
      <div className="carousel-nav">
        <button
          type="button"
          className="carousel-nav__btn carousel-nav__btn--prev"
          onClick={goToPrevItem}
          disabled={!curItem}
        >
          ⟪
        </button>
        <button
          type="button"
          className="carousel-nav__btn carousel-nav__btn--next"
          onClick={goToNextItem}
          disabled={curItem === quotes.length - 1}
        >
          ⟫
        </button>
      </div>
    </div>
  );
}

export default Carousel;
