import React, { Fragment } from "react";
import ReactSimpleCarousel from "react-plain-carousel";
import EachItem from "./EachItem";
import { sliderdata } from "./demoJson";
class Carosal extends React.Component {
  state = {
    carousel: {},
    index: 0,
    listitem: [],
  };

  next = () => {
    this.state.carousel.next();
  };

  prev = () => {
    this.state.carousel.prev();
  };

  moveTo = (index) => () => {
    this.state.carousel.moveTo(index);
  };

  handleMount = (carousel) => {
    this.setState({ carousel });
  };

  handleTransitionEnd = ({ index }) => {
    this.setState({ index });
  };
  list = () => {
    return this.props.quotes.slice(0, 2).map((quote) => {
      return (
        <div style={{ height: 50, backgroundColor: "red" }}>{quote.author}</div>
      );
    });
  };

  render() {
    return (
      <Fragment>
        {console.log(this.props.quotes)}
