        <h2>Carousel 2 (controls)</h2>
        <Fragment>
          <ReactSimpleCarousel
            isInfinity
            className="carousel2"
            onMount={this.handleMount}
          >
            {sliderdata.map((item, index) => {
              return (
                <div style={{ height: 250 }}>
                  {item.text}
                </div>
              );
            })}
          </ReactSimpleCarousel>

          <button onClick={this.prev}>Prev</button>
          <button onClick={this.next}>Next</button>
        </Fragment>
      </Fragment>
    );
  }
}
export default Carosal;
